    question_text, correct_answer, answer_choices, explanation, answer_analysis, is_active
) VALUES (
    '35ec767c',
    'SAT',
    'Reading and Writing',
    'Information and Ideas',
    'Hard',
    'Command of Evidence',
    'Corn-Related Vocabulary in Various Southeastern Languages
